import React from 'react'
import Element from '../Element'
import styles from './index.css'

export default function ComposedElement({ elements, onClick }) {
  return (
    <div className={styles.ComposedElement}>
      {elements.map(element => (
        <Element
          key={element.id}
          id={element.id}
          type={element.type}
          text={element.text}
          url={element.url}
          postback={element.postback}
          elements={element.elements}
          onClick={onClick}
        />
      )
      )}
    </div>
  )
}
