import React from 'react'
import styles from './index.css'
import Button from '../Button'

export default function QuickReply ({ replies, onClick }) {
  return (
    <div className={styles.QuickReplies}>
      {replies.map(({text, id, postback}) => {
        return <Button key={id} label={text} postback={postback} onClick={onClick} />
      })}
    </div>
  )
}
