import React from 'react'
import ComposedElement from './Element/ComposedElement'
import styles from './index.css'
import classname from 'classnames'

export default function Plugin ({ elements }) {
  const isItCarusel = elements.length > 1
  const caruselClass = classname({ [styles.Carusel]: isItCarusel })

  return (
    <div class={styles.PluginFrameWrapper}>
      {isItCarusel && <div className={styles.LeftRow} />}
      <div className={styles.PluginFrame}>
        <div className={caruselClass}>
          {elements.map(element => (
            <div class={styles.Plugin}>
              <ComposedElement elements={element.elements} />
            </div>
          ))}
        </div>
      </div>
      {isItCarusel && <div className={styles.RightRow} />}
    </div>
  )
}
