import React, { Component } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames/bind';
import styles from './Statuses.css';

const cx = classNames.bind(styles);

export default class Statuses extends Component {

  static get propTypes() {
    return {
      text: PropTypes.string,
      Orange: PropTypes.boolean,
      Yellow: PropTypes.boolean,
      Green: PropTypes.boolean,
      Blue: PropTypes.boolean,
      Purple: PropTypes.boolean,
      Red: PropTypes.boolean,
      Grey: PropTypes.boolean,
      Reversed: PropTypes.boolean
    };
  };

  render() {
    const { Orange, Yellow, Green, Blue, Purple, Red, Grey, Reversed } = this.props;

    const className = cx(styles.Statuses, {
      Orange,
      Yellow,
      Green,
      Blue,
      Purple,
      Red,
      Grey,
      Reversed
    });

    return (
      <div className={className}>
        {this.props.text}
      </div>
    );
  }
}

